import express, {Request, Response} from "express";
import * as dotenv from "dotenv";
import mysql from "mysql2";
import {api} from "./api";
import {config} from "./config";
import multer from "multer";
import bodyParser from "body-parser";
import cors from "cors";
import path from "path";
import Minio from 'minio';

const _minio = require("minio");

export let production:boolean = true;
export let testRun:boolean = false;

const productionFalse = 'production=false';
const testRunTrue = 'test=true';

try {
    JSON.parse(process.env.PRODUCTION as string) === false ? production=false:production=true;
} catch(e){
    // Suppress error thrown by attempting to parse an undefined  variable.
    // Error: "Unexpected token u in JSON at position 0"
}; 

process.argv.forEach((arg:string,k:number)=>{
    switch(arg){
    case productionFalse:
        production = false;
        break;
    case testRunTrue:
        testRun = true;
        break;
    }
});

export let s3Client: Minio.Client;

var storage = multer.diskStorage({
    destination:'./bin/images',
    filename: function(r,fl,cb) {
        r.file = fl;
        const name = `${fl.fieldname}_${Date.now()}.${fl.mimetype.split('/')[1]}`;
        cb(null,name);
    }
});

var upload = multer({storage:storage});

dotenv.config();

export const app = express();

app.use(cors());

const port = parseInt(process.env.PORT as string,10);

export var db: mysql.Pool = mysql.createPool({
    host: process.env.DB_HOST,
    port: parseInt(process.env.DB_PORT as string,10),
    database: process.env.DB_NAME,
    user: process.env.DB_USER,
    password: process.env.DB_PASSWORD
});

export function setDbMock(obj:any) {
    db = obj;
}

app.use(express.json());

app.use(bodyParser.urlencoded({extended:true,limit:10000000}));

app.use('/images',express.static('bin/images'));
app.use('/static/js',express.static('bin/client_dist/static/js'));
app.use('/static/css',express.static('bin/client_dist/static/css'));

export let _api: api = new api();

app.get('/',_api.index);
app.get('/items',_api.getItems);
app.get('/orders',_api.getOrders);

app.get('/item/:id',_api.getItem);
app.post('/item',_api.postItem);
app.patch('/item',_api.patchItem);
app.delete('/item/:id',_api.deleteItem);

app.get('/order/:id',_api.getOrder);
app.post('/order',_api.postOrder);
app.patch('/order',_api.patchOrder);
app.delete('/order/:id',_api.deleteOrder);

app.post('/login',_api.login);
app.post('/register',_api.register);

app.get('/user/:id',_api.getUser);
app.get('/users',_api.getUsers);
if(process.env.S3_ENABLE === "true") {
    s3Client = new _minio.Client({
        endPoint: process.env.S3_ENDPOINT as string,
        port: parseInt(process.env.S3_PORT as string,10),
        useSSL: JSON.parse(process.env.S3_USESSL as string),
        accessKey: process.env.S3_ACCESSKEY as string,
        secretKey: process.env.S3_SECRETKEY as string
    });
        s3Client.makeBucket(process.env.S3_BUCKET as string,process.env.S3_REGION as string,(err:any)=>{err ? console.log(err.message) : null});
    app.post('/images',multer({storage:multer.memoryStorage()}).single('image'),_api.uploadImagesS3);
} else {
    app.post('/images',upload.single('image'),_api.uploadImages);
}

app.get('/verify',_api.verify)

export let server = app.listen(port, ()=> {

    console.debug(`[production]=(${production})\n[test_run]=(${testRun})\n`);

    console.log(`Listening on port ${port}...`)

});

function forceShutdown() {
    setTimeout(()=>{
        console.log("Gracefully shutting down failed, forcing shutdown...");
        process.exit(1);
    },3000);
}

export function shutdown(): void {
    console.log("Server closing")
    if(server) {
        server.close((err:any)=>{
            if(err) {
                console.log(err.message);
            }
        })
    }
    if (db) {
    db.removeAllListeners();
    db.end((err:any)=>{
            if(err) {
                db.removeAllListeners();
                console.log(err.message);
            }
        })
    }
    forceShutdown();
}

process.on('SIGINT',()=>{
    console.info('Sigint received')
    shutdown();
});

process.on('SIGTERM',()=>{
    console.info('Sigterm received')
    shutdown();
});

import express, {Request, Response, Router} from "express";
import {config} from "./config";
import {db,s3Client} from "./main";
import PoolConnection from "mysql2/typings/mysql/lib/PoolConnection";
import { compare, hash } from "./auth";
import * as jwt from "jsonwebtoken";
import SMTPConnection from "nodemailer/lib/smtp-connection";

export const cfg = new config();

let connection: SMTPConnection = new SMTPConnection({
    port:parseInt(process.env.SMTP_PORT as string,10),
    host:process.env.SMTP_HOST as string,
    secure: true
});

export function handleError(err:Error | undefined, conn: PoolConnection, wr: Response) {
    if(err) {
        console.log(err.message);
        conn.release();
        wr.end();
    }
}

export function handleUrlParamReq(r: Request, wr: Response, queryType: string, resource: string) {
    console.log(r.params);
    let n = parseInt(r.params.id,10);
    db.getConnection((err,conn)=>{
        if(err) {
            handleError(err,conn,wr)
            return;
        }
        conn.query(cfg.schema.queries[queryType][resource],n,(err,result,fields)=>{
            if (err) {
                handleError(err,conn,wr)
                return;
            }
            console.log(result);
            wr.setHeader("Content-Type","application/json");
            wr.write(JSON.stringify(result));
            conn.release();
            wr.end();
        });
    });
}

export function handleSelectAll(r: Request, wr: Response, resource: string) {
    db.getConnection((err,conn)=> {
        if(err) {
            handleError(err,conn,wr)
            return;
        }
        conn.query(cfg.schema?.queries?.select[resource],(err,result,fields)=> {
            if (err) {
                handleError(err,conn,wr)
                return;
            }
            console.log(result);
            wr.setHeader("Content-Type","application/json");
            wr.write(JSON.stringify(result));
            conn.release();
            wr.end();
        });
    });
}

export function verify(r: Request, wr: Response): boolean {
    try{
        if (jwt.verify(r.headers?.authorization?.split(' ')[1] as string,cfg.secret) === undefined) {
            wr.writeHead(403,"Forbidden");
            console.log('jwt invalid token')
            wr.end();
            return false;
        }
        return true;
    }catch(e:any){
        console.log(e);
        wr.writeHead(403,"Forbidden");
        console.log('jwt invalid token')
        wr.end();
        return false;
    }
}

function insertImage(r: Request, wr: Response, url: string) {
    let values = {
        item_id: r.body.item_id,
        url: url
    }
    db.getConnection((err,conn)=>{
        if(err){
            handleError(err,conn,wr);
            return;
        }
        conn.query(cfg.schema.queries?.insert?.image,values,(err,result,fields)=>{
            if(err){
                handleError(err,conn,wr);
                return;
            }
            console.log(result);
            wr.setHeader("Content-Type","application/json");
            wr.write(JSON.stringify({result,values}));
            wr.end();
        });
        conn.release();
    });
}

export class api {
    constructor() {
            
    }

    
    async getItems(r: Request, wr: Response) {
        handleSelectAll(r,wr,'all_items');
    }

    
    async getOrders(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        handleSelectAll(r,wr,'all_orders');
    }

    
    async index(r: Request, wr: Response) {
        console.log(cfg.template);
        wr.write(`${cfg.template}`);
        wr.end();
    }

    /*
    
    serveBundleJS(r: Request, wr: Response) {
        wr.setHeader("Content-Type","application/javascript");
        wr.write(cfg.appjs);
        wr.end();
    }
    */

    
    async getItem(r: Request, wr: Response) {
        handleUrlParamReq(r,wr,'select','item');
    }

    
    async getUser(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        handleUrlParamReq(r,wr,'select','user');
    }

    
    async getUsers(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        handleSelectAll(r,wr,'all_users');
    }

    
    async deleteItem(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        handleUrlParamReq(r,wr,'delete','item');
    }

    
    async getOrder(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        handleUrlParamReq(r,wr,'select','order');
    }

    
    async deleteOrder(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        handleUrlParamReq(r,wr,'delete','order');
    }

    
    async postItem(r: Request, wr: Response) {
        let q: string = cfg.schema.queries.insert.item
        if(!verify(r,wr)) {
            return;
        }
        db.getConnection((err,conn)=>{
            conn.query(q ,r.body ,(err,result,fields)=>{
                if(err){
                    handleError(err,conn,wr)
                    return;
                }
                wr.write(JSON.stringify(result));
                wr.end();
            });
            conn.release();
        });
    }

    
    async patchItem(r: Request, wr: Response) {
        console.log(r.body);
        if(!verify(r,wr)) {
            return;
        }
        db.getConnection((err,conn)=>{
            if(err) {
                handleError(err,conn,wr)
                return;
            }
            conn.query(cfg.schema.queries.update.item,r.body,(err,result,fields)=>{
                if (err) {
                    handleError(err,conn,wr)
                    return;
                }
                console.log(result);
                wr.setHeader("Content-Type","application/json");
                wr.write(JSON.stringify(result));
                wr.end();
            });
            conn.release();
        });
    }

    
    async postOrder(r: Request, wr: Response) {
        db.getConnection((err: Error, conn: PoolConnection)=>{
            handleError(err,conn,wr);
            conn.query(cfg.schema.queries.insert.order,r.body,(err,result,fields)=>{
                if(err) {
                    handleError(err,conn,wr);
                    return;
                }
                try{
                connection.connect((err)=>{
                    if(err) {
                        console.log("Connection error:",err);
                        return;
                    }
                    if(connection.alreadySecured) {
                        console.log('Secure connection to SMTP server established');
                    }
                    connection.login({credentials:{user:process.env.SMTP_USER as string,pass:process.env.SMTP_PASS as string}},(err)=>{
                        if(err) {
                            console.log("Login error:",err);
                            return;
                        }
                        console.log(r.body);
                        let details = JSON.parse(r.body.details);
                        let units = "";
                        for(let i=0;i<details.items.length;i++) {
                            units += `\n
                            Name: ${details?.items[i]?.name} Price/unit: ${details?.items[i]?.price} Amount: ${details?.items[i]?.count} \n`
                        } 
                        connection.send({
                            from:process.env.SMTP_USER as string,
                            to:details.email,
                        },`Subject: Order ${r.body.name} 
                        \n\n\n
                        Transaction status: ${details.transaction.details.status} \n
                        Transaction ID: ${details.transaction.details.id} \n
                        Details:
                        ${units} \n
                        Total: ${details?.total} ${details?.transaction?.details?.purchase_units[0]?.amount?.currency_code} \n
                        `
                        ,(err,info)=>{
                            if(err) {
                                console.log("Send error:",err);
                            }
                            console.log(info);
                            connection.quit();
                        });
                    });
                });
                } catch(e:any) {console.log(e.message)}
                console.log(result);
                wr.setHeader("Content-Type","application/json");
                wr.write(JSON.stringify(result));
                wr.end();
            });
            conn.release();
        })
    }

    
    async patchOrder(r: Request, wr: Response) {
        console.log(r.body);
        db.getConnection((err,conn)=>{
            if(err) {
                handleError(err,conn,wr)
                return;
            }
            conn.query(cfg.schema.queries.patch.order_paid,r.body,(err,result,fields)=>{
                if (err) {
                    handleError(err,conn,wr)
                    return;
                }
                console.log(result);
                wr.setHeader("Content-Type","application/json");
                wr.write(JSON.stringify(result));
                wr.end();
            });
            conn.release();
        });
    }

    
    async login(r: Request, wr: Response) {
        console.log(r.body);
        db.getConnection((err,conn)=>{
            if(err || !r.body.email || !r.body.password) {
                console.log(err);
                err ? wr.writeHead(500,'Internal server error') : wr.writeHead(403,'Invalid credentials');
                wr.end();
                return;
            }
            console.log("query",cfg.schema.queries.select.login);
            conn.query(cfg.schema.queries.select.login,r.body.email,(err,result: any,fields)=>{
                if (err) {
                    console.log("login email query for hash",err);
                    wr.writeHead(403,'Forbidden');
                    wr.end();
                    return;
                }
                if (result[0].password !== undefined && compare(r.body?.password,result[0].password)) {
                    let token = jwt.sign({id:result.id,email:result.email},cfg.secret);
                    wr.setHeader("Content-Type","application/json");
                    wr.write(JSON.stringify({Authorization: token}));
                    wr.end();
                } else {
                    wr.writeHead(403,"Forbidden");
                    wr.end()
                }
                conn.release();
            });
        });
    }

    
    async register(r: Request, wr: Response) {
        console.log(r.body);
        db.getConnection((err: Error, conn: PoolConnection)=>{
            if(err || !r.body.password) {
                console.log(err);
                err ? wr.writeHead(500,'Internal server error') : wr.writeHead(403,'No password provided');
                wr.end();
                return;
            };
            r.body.password = hash(r.body.password.trimLeft().trimRight());
            conn.query(cfg.schema.queries.insert.user,{email:r.body.email,password:r.body.password,role:0},(err,result,fields)=>{
                if(err) {
                    console.log(err);
                    wr.writeHead(403,'Forbidden');
                    wr.end();
                    return;
                }
                console.log(result);
                wr.setHeader("Content-Type","application/json");
                wr.write(JSON.stringify(result));
                conn.release();
                wr.end();
            });
        });
    }

     // local server filesystem
    async uploadImages(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        console.log(r);
        insertImage(r,wr,`/images/${r.file?.filename}`);
    }

     // S3 compatible storage
    async uploadImagesS3(r: Request, wr: Response) {
        if(r.file && verify(r,wr)) {
            console.log(r.file);
            try {
                await s3Client.putObject(process.env.S3_BUCKET as string,r.file?.originalname,r.file?.buffer);
                const url = await s3Client.presignedGetObject(process.env.S3_BUCKET as string,r.file?.originalname);
                insertImage(r,wr,url);
            } catch(e:any) {
                console.log('uploadImagesS3 failed:',e);
            }
        }
    }

    
    async verify(r: Request, wr: Response) {
        if(!verify(r,wr)) {
            return;
        }
        wr.write(JSON.stringify({status:'valid'}));
        wr.end();
    }
}

import * as fs from 'fs';
import * as dotenv from "dotenv";
import { db, testRun } from './main';
import { migrate as seedTestData } from './load.test.data';
import Pool from 'mysql2/typings/mysql/lib/Pool';

dotenv.config();

// Wait until the object is populated, wait() will return the object when it is populated;
function wait(obj: any, fn: any, checkInterval?: number): void {
    checkInterval ? checkInterval : checkInterval = 300;
    let i = setInterval(()=>{
        obj !== undefined ? (()=>{clearInterval(i); fn(obj);}) : null;
    }, checkInterval);
}

export class config {
    public schema: any = {};
    public template: string = "";
    public secret: string = "";

    constructor() {
        let data = fs.readFileSync(process.env.DB_Q as string,'utf8',);
        this.schema = JSON.parse(data);
        this.template = fs.readFileSync(process.env.ENTRYPOINT as string,'utf8');
        this.secret = process.env.SECRET as string;
        wait(db,(pool:Pool)=>{
            this.migrate(this.schema.migrate_up);
            testRun ? seedTestData(): null;
        });
    }

    migrate(obj: any): any | undefined {
            let keys = Object.keys(obj)
            keys.forEach((v,i)=>{
                // console.log(`Creating table ${v}`);
                db.query(obj[v],(err,result,fields)=>{
                    if(err) {
                        return err;
                    }
                });
            });
    }
}

import {db} from './main';
import * as fs from "fs";
import { RowDataPacket } from 'mysql2';
import PoolConnection from 'mysql2/typings/mysql/lib/PoolConnection';

export function migrate() {
    let q = JSON.parse(fs.readFileSync('./test/test_seed.sql').toString());
    db.getConnection((err:any,conn:PoolConnection)=>{
        if(err) {
            console.log(err.message);
            return;
        }
        conn.beginTransaction((err:any)=>{
            if(err) {
                console.log(err.message);
                conn.release();
                return;
            }
            q.forEach((insert:string)=>{
                conn.query(insert,(err,result:RowDataPacket)=>{
                    if(err) {
                        console.warn(err.message);
                        conn.rollback(()=>{
                            conn.release();
                        });
                        return;
                    }
                    // console.log(result);
                });
            });
        });
        conn.commit((err:any)=>err ? console.log(err.message) : console.log("test migrate success"));
    });

}

import bcryptjs from "bcryptjs";

export function hash(plain: string): string {
    return bcryptjs.hashSync(plain, 11);
}

export function compare(plain: string, h: string): boolean {
    return bcryptjs.compareSync(plain,h);
}
